# prints out how many MBs/GBs have been shared since last restart of bitcoin-node
bitcoin-cli getpeerinfo | grep "bytessent" | grep -o "\d*" | awk -v uptime=$(bitcoin-cli uptime) 'BEGIN {printf "\n#==================================================#\n####### Bitcoin Node: bytes shared per PEER #######\n#==================================================#\n"}{ print "\t\tPeer #" i++, $1;  SUM += $1; } END { printf "\n ‚Çø blocks shared with plebs:[%d MBs/%.2f GBs] ü§ù \n Node last restarted: [%d min/%d hr] ago ‚ö°Ô∏è\n", SUM/1000000, SUM/1000000000, uptime/60, uptime/(60*60) }'
